head.script:
/*

TEMPLATE ::= "[" NODE_NAME { ["," TEXT] ["," TEMPLATE] } { ["," KEY "," VALUE] } "]"
           | "[" TEMPLATE { "," TEMPLATE } "]"

NODE_NAME :: = <dom string>
TEXT :: = <dom string>
KEY :: = <dom string>
VALUE :: = <any js object except an array. a string value will by set with setAttribute(key, value), any other object as js property on the object>

*/

var templates =
{
  test_1: function()
  {
    return \
    ['p', 'some text']
  },
  test_2: function()
  {
    return \
    [
      'div', 
        'some text',
        ['p', 'paragraph 1'],
        'some text',
        ['p', 'paragraph 2'],
        'some text',
        ['p', 'paragraph 3']
    ]
  },
  test_3: function()
  {
    return \
    [
      'div', 
        'some text',
        ['p', 'paragraph 1'],
        'some text',
        ['p', 'paragraph 2'],
        'some text',
        ['p', 'paragraph 3']
      'class', 'foo'
    ]
  },
  test_4: function()
  {
    return \
    [ 
      ['p', 'paragraph 1'],
      ['p', 'paragraph 2'],
      ['p', 'paragraph 3']
    ]
  },
  test_5: function()
  {
    return \
    [
      'p', 
        ['input',
          'type', 'button',
          'value', 'click me',
          'onclick', function(event)
          {
            alert(event.target.nodeName);
          }
        ]
    ]
  }
}


onload = function()
{
  var 
  template = null,
  body =  document.getElementsByTagName('body')[0];

  for (template in templates)
  {
    body.render(['h2', template]);
    body.render(templates[template]());
  }
}
